// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "./interfaces/AdapterInterface.sol";
import "../external/interfaces/WETH9Interface.sol";
import "../libraries/CircleCCTPAdapter.sol";
import "../external/interfaces/CCTPInterfaces.sol";

// @dev Use local modified CrossDomainEnabled contract instead of one exported by eth-optimism because we need
// this contract's state variables to be `immutable` because of the delegateCall call.
import "./CrossDomainEnabled.sol";
import "@eth-optimism/contracts/L1/messaging/IL1StandardBridge.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @notice Interface for Synthetix custom bridge to Optimism.
 */
interface SynthetixBridgeToOptimism is IL1StandardBridge {
    /**
     * @notice Send tokens to Optimism.
     * @param to Address to send tokens to on L2.
     * @param amount Amount of tokens to send.
     */
    function depositTo(address to, uint256 amount) external;
}

/**
 * @notice Contract containing logic to send messages from L1 to Optimism.
 * @dev Public functions calling external contracts do not guard against reentrancy because they are expected to be
 * called via delegatecall, which will execute this contract's logic within the context of the originating contract.
 * For example, the HubPool will delegatecall these functions, therefore it's only necessary that the HubPool's methods
 * that call this contract's logic guard against reentrancy.
 */

// solhint-disable-next-line contract-name-camelcase
contract Optimism_Adapter is CrossDomainEnabled, AdapterInterface, CircleCCTPAdapter {
    using SafeERC20 for IERC20;
    uint32 public constant L2_GAS_LIMIT = 200_000;

    WETH9Interface public immutable L1_WETH;

    IL1StandardBridge public immutable L1_STANDARD_BRIDGE;

    // Optimism has the ability to support "custom" bridges. These bridges are not supported by the canonical bridge
    // and so we need to store the address of the custom token and the associated bridge. In the event we want to
    // support a new token that is not supported by Optimism, we can add a new custom bridge for it and re-deploy the
    // adapter. A full list of custom optimism tokens and their associated bridges can be found here:
    // https://github.com/ethereum-optimism/ethereum-optimism.github.io/blob/master/optimism.tokenlist.json
    address public constant DAI = 0x6B175474E89094C44Da98b954EedeAC495271d0F;
    address public constant DAI_OPTIMISM_BRIDGE = 0x10E6593CDda8c58a1d0f14C5164B376352a55f2F;
    address public constant SNX = 0xC011a73ee8576Fb46F5E1c5751cA3B9Fe0af2a6F;
    address public constant SNX_OPTIMISM_BRIDGE = 0x39Ea01a0298C315d149a490E34B59Dbf2EC7e48F;

    /**
     * @notice Constructs new Adapter.
     * @param _l1Weth WETH address on L1.
     * @param _crossDomainMessenger XDomainMessenger Optimism system contract.
     * @param _l1StandardBridge Standard bridge contract.
     * @param _l1Usdc USDC address on L1.
     * @param _cctpTokenMessenger TokenMessenger contract to bridge via CCTP.
     */
    constructor(
        WETH9Interface _l1Weth,
        address _crossDomainMessenger,
        IL1StandardBridge _l1StandardBridge,
        IERC20 _l1Usdc,
        ITokenMessenger _cctpTokenMessenger
    )
        CrossDomainEnabled(_crossDomainMessenger)
        CircleCCTPAdapter(_l1Usdc, _cctpTokenMessenger, CircleDomainIds.Optimism)
    {
        L1_WETH = _l1Weth;
        L1_STANDARD_BRIDGE = _l1StandardBridge;
    }

    /**
     * @notice Send cross-chain message to target on Optimism.
     * @param target Contract on Optimism that will receive message.
     * @param message Data to send to target.
     */
    function relayMessage(address target, bytes calldata message) external payable override {
        sendCrossDomainMessage(target, L2_GAS_LIMIT, message);
        emit MessageRelayed(target, message);
    }

    /**
     * @notice Bridge tokens to Optimism.
     * @param l1Token L1 token to deposit.
     * @param l2Token L2 token to receive.
     * @param amount Amount of L1 tokens to deposit and L2 tokens to receive.
     * @param to Bridge recipient.
     */
    function relayTokens(
        address l1Token,
        address l2Token,
        uint256 amount,
        address to
    ) external payable override {
        // If the l1Token is weth then unwrap it to ETH then send the ETH to the standard bridge.
        if (l1Token == address(L1_WETH)) {
            L1_WETH.withdraw(amount);
            L1_STANDARD_BRIDGE.depositETHTo{ value: amount }(to, L2_GAS_LIMIT, "");
        }
        // If the l1Token is USDC, then we send it to the CCTP bridge
        else if (_isCCTPEnabled() && l1Token == address(usdcToken)) {
            _transferUsdc(to, amount);
        } else {
            address bridgeToUse = address(L1_STANDARD_BRIDGE);

            // Check if the L1 token requires a custom bridge. If so, use that bridge over the standard bridge.
            if (l1Token == DAI) bridgeToUse = DAI_OPTIMISM_BRIDGE; // 1. DAI
            if (l1Token == SNX) bridgeToUse = SNX_OPTIMISM_BRIDGE; // 2. SNX

            IERC20(l1Token).safeIncreaseAllowance(bridgeToUse, amount);
            if (l1Token == SNX) SynthetixBridgeToOptimism(bridgeToUse).depositTo(to, amount);
            else IL1StandardBridge(bridgeToUse).depositERC20To(l1Token, l2Token, to, amount, L2_GAS_LIMIT, "");
        }
        emit TokensRelayed(l1Token, l2Token, amount, to);
    }
}

contracts/chain-adapters/Polygon_Adapter.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "./interfaces/AdapterInterface.sol";
import "../external/interfaces/WETH9Interface.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "../libraries/CircleCCTPAdapter.sol";
import "../external/interfaces/CCTPInterfaces.sol";

/**
 * @notice Send tokens to Polygon.
 */
interface IRootChainManager {
    /**
     * @notice Send msg.value of ETH to Polygon
     * @param user Recipient of ETH on Polygon.
     */
    function depositEtherFor(address user) external payable;

    /**
     * @notice Send ERC20 tokens to Polygon.
     * @param user Recipient of L2 equivalent tokens on Polygon.
     * @param rootToken L1 Address of token to send.
     * @param depositData Data to pass to L2 including amount of tokens to send. Should be abi.encode(amount).
     */
    function depositFor(
        address user,
        address rootToken,
        bytes calldata depositData
    ) external;
}

/**
 * @notice Send arbitrary messages to Polygon.
 */
interface IFxStateSender {
    /**
     * @notice Send arbitrary message to Polygon.
     * @param _receiver Address on Polygon to receive message.
     * @param _data Message to send to `_receiver` on Polygon.
     */
    function sendMessageToChild(address _receiver, bytes calldata _data) external;
}

/**
 * @notice Similar to RootChainManager, but for Matic (Plasma) bridge.
 */
interface DepositManager {
    /**
     * @notice Send tokens to Polygon. Only used to send MATIC in this Polygon_Adapter.
     * @param token L1 token to send. Should be MATIC.
     * @param user Recipient of L2 equivalent tokens on Polygon.
     * @param amount Amount of `token` to send.
     */
    function depositERC20ForUser(
        address token,
        address user,
        uint256 amount
    ) external;
}

/**
 * @notice Sends cross chain messages Polygon L2 network.
 * @dev Public functions calling external contracts do not guard against reentrancy because they are expected to be
 * called via delegatecall, which will execute this contract's logic within the context of the originating contract.
 * For example, the HubPool will delegatecall these functions, therefore its only necessary that the HubPool's methods
 * that call this contract's logic guard against reentrancy.
 */

// solhint-disable-next-line contract-name-camelcase
contract Polygon_Adapter is AdapterInterface, CircleCCTPAdapter {
    using SafeERC20 for IERC20;
    IRootChainManager public immutable ROOT_CHAIN_MANAGER;
    IFxStateSender public immutable FX_STATE_SENDER;
    DepositManager public immutable DEPOSIT_MANAGER;
    address public immutable ERC20_PREDICATE;
    address public immutable L1_MATIC;
    WETH9Interface public immutable L1_WETH;

    /**
     * @notice Constructs new Adapter.
     * @param _rootChainManager RootChainManager Polygon system contract to deposit tokens over the PoS bridge.
     * @param _fxStateSender FxStateSender Polygon system contract to send arbitrary messages to L2.
     * @param _depositManager DepositManager Polygon system contract to deposit tokens over the Plasma bridge (Matic).
     * @param _erc20Predicate ERC20Predicate Polygon system contract to approve when depositing to the PoS bridge.
     * @param _l1Matic matic address on l1.
     * @param _l1Weth WETH address on L1.
     * @param _l1Usdc USDC address on L1.
     * @param _cctpTokenMessenger TokenMessenger contract to bridge via CCTP.
     */
    constructor(
        IRootChainManager _rootChainManager,
        IFxStateSender _fxStateSender,
        DepositManager _depositManager,
        address _erc20Predicate,
        address _l1Matic,
        WETH9Interface _l1Weth,
        IERC20 _l1Usdc,
        ITokenMessenger _cctpTokenMessenger
    ) CircleCCTPAdapter(_l1Usdc, _cctpTokenMessenger, CircleDomainIds.Polygon) {
        ROOT_CHAIN_MANAGER = _rootChainManager;
        FX_STATE_SENDER = _fxStateSender;
        DEPOSIT_MANAGER = _depositManager;
        ERC20_PREDICATE = _erc20Predicate;
        L1_MATIC = _l1Matic;
        L1_WETH = _l1Weth;
    }

    /**
     * @notice Send cross-chain message to target on Polygon.
     * @param target Contract on Polygon that will receive message.
     * @param message Data to send to target.
     */

    function relayMessage(address target, bytes calldata message) external payable override {
        FX_STATE_SENDER.sendMessageToChild(target, message);
        emit MessageRelayed(target, message);
    }

    /**
     * @notice Bridge tokens to Polygon.
     * @param l1Token L1 token to deposit.
     * @param l2Token L2 token to receive.
     * @param amount Amount of L1 tokens to deposit and L2 tokens to receive.
     * @param to Bridge recipient.
     */
    function relayTokens(
        address l1Token,
        address l2Token,
        uint256 amount,
        address to
    ) external payable override {
        // If the l1Token is weth then unwrap it to ETH then send the ETH to the standard bridge.
        if (l1Token == address(L1_WETH)) {
            L1_WETH.withdraw(amount);
            ROOT_CHAIN_MANAGER.depositEtherFor{ value: amount }(to);
        }
        // If the l1Token is USDC, then we send it to the CCTP bridge
        else if (_isCCTPEnabled() && l1Token == address(usdcToken)) {
            _transferUsdc(to, amount);
        } else if (l1Token == L1_MATIC) {
            IERC20(l1Token).safeIncreaseAllowance(address(DEPOSIT_MANAGER), amount);
            DEPOSIT_MANAGER.depositERC20ForUser(l1Token, to, amount);
        } else {
            IERC20(l1Token).safeIncreaseAllowance(ERC20_PREDICATE, amount);
            ROOT_CHAIN_MANAGER.depositFor(to, l1Token, abi.encode(amount));
        }
        emit TokensRelayed(l1Token, l2Token, amount, to);
    }
}

contracts/chain-adapters/PolygonZkEVM_Ad.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "./interfaces/AdapterInterface.sol";
import "../external/interfaces/WETH9Interface.sol";
import "../external/interfaces/IPolygonZkEVMBridge.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// solhint-disable-next-line contract-name-camelcase
contract PolygonZkEVM_Adapter is AdapterInterface {
    using SafeERC20 for IERC20;

    WETH9Interface public immutable L1_WETH;
    // Address of Polygon zkEVM's Canonical Bridge on L1.
    IPolygonZkEVMBridge public immutable L1_POLYGON_ZKEVM_BRIDGE;

    // Polygon's internal network id for zkEVM.
    uint32 public constant POLYGON_ZKEVM_L2_NETWORK_ID = 1;

    /**
     * @notice Constructs new Adapter.
     * @param _l1Weth WETH address on L1.
     * @param _l1PolygonZkEVMBridge Canonical token bridge contract on L1.
     */
    constructor(WETH9Interface _l1Weth, IPolygonZkEVMBridge _l1PolygonZkEVMBridge) {
        L1_WETH = _l1Weth;
        L1_POLYGON_ZKEVM_BRIDGE = _l1PolygonZkEVMBridge;
    }

    /**
     * @notice Send cross-chain message to target on Polygon zkEVM.
     * @param target Contract on Polygon zkEVM that will receive message.
     * @param message Data to send to target.
     */
    function relayMessage(address target, bytes calldata message) external payable override {
        L1_POLYGON_ZKEVM_BRIDGE.bridgeMessage(POLYGON_ZKEVM_L2_NETWORK_ID, target, true, message);
        emit MessageRelayed(target, message);
    }

    /**
     * @notice Bridge tokens to Polygon zkEVM.
     * @param l1Token L1 token to deposit.
     * @param l2Token L2 token to receive.
     * @param amount Amount of L1 tokens to deposit and L2 tokens to receive.
     * @param to Bridge recipient.
     */
    function relayTokens(
        address l1Token,
        address l2Token,
        uint256 amount,
        address to
    ) external payable override {
        // The mapped WETH address in the native Polygon zkEVM bridge contract does not match
        // the official WETH address. Therefore, if the l1Token is WETH then unwrap it to ETH
        // and send the ETH directly via as msg.value.
        if (l1Token == address(L1_WETH)) {
            L1_WETH.withdraw(amount);
            L1_POLYGON_ZKEVM_BRIDGE.bridgeAsset{ value: amount }(
                POLYGON_ZKEVM_L2_NETWORK_ID,
                to,
                amount,
                address(0),
                true,
                ""
            );
        } else {
            IERC20(l1Token).safeIncreaseAllowance(address(L1_POLYGON_ZKEVM_BRIDGE), amount);
            L1_POLYGON_ZKEVM_BRIDGE.bridgeAsset(POLYGON_ZKEVM_L2_NETWORK_ID, to, amount, l1Token, true, "");
        }

        emit TokensRelayed(l1Token, l2Token, amount, to);
    }
}

contracts/chain-adapters/Scroll_Adapter..content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@scroll-tech/contracts/L1/gateways/IL1GatewayRouter.sol";
import "@scroll-tech/contracts/L1/rollup/IL2GasPriceOracle.sol";
import "@scroll-tech/contracts/L1/IL1ScrollMessenger.sol";
import "./interfaces/AdapterInterface.sol";

/**
 * @title Scroll_Adapter
 * @notice Adapter contract deployed on L1 alongside the HubPool to facilitate token transfers
 * and arbitrary message relaying from L1 to L2.
 */
contract Scroll_Adapter is AdapterInterface {
    using SafeERC20 for IERC20;

    /**
     * @notice Used as the gas limit for relaying messages to L2.
     */
    uint32 public immutable L2_MESSAGE_RELAY_GAS_LIMIT;

    /**
     * @notice Use as the gas limit for relaying tokens to L2.
     */
    uint32 public immutable L2_TOKEN_RELAY_GAS_LIMIT;

    /**
     * @notice The address of the official l1GatewayRouter contract for Scroll for bridging tokens from L1 -> L2
     * @dev We can find these (main/test)net deployments here: https://docs.scroll.io/en/developers/scroll-contracts/#scroll-contracts
     */
    IL1GatewayRouter public immutable L1_GATEWAY_ROUTER;

    /**
     * @notice The address of the official messenger contract for Scroll from L1 -> L2
     * @dev We can find these (main/test)net deployments here: https://docs.scroll.io/en/developers/scroll-contracts/#scroll-contracts
     */
    IL1ScrollMessenger public immutable L1_SCROLL_MESSENGER;

    /**
     * @notice The address of the official gas price oracle contract for Scroll for estimating the relayer fee
     * @dev We can find these (main/test)net deployments here: https://docs.scroll.io/en/developers/scroll-contracts/#scroll-contracts
     */
    IL2GasPriceOracle public immutable L2_GAS_PRICE_ORACLE;

    /**************************************
     *          PUBLIC FUNCTIONS          *
     **************************************/

    /**
     * @notice Constructs new Adapter.
     * @param _l1GatewayRouter Standard bridge contract.
     * @param _l1ScrollMessenger Scroll Messenger contract.
     * @param _l2GasPriceOracle Gas price oracle contract.
     * @param _l2MessageRelayGasLimit Gas limit for relaying messages to L2.
     * @param _l2TokenRelayGasLimit Gas limit for relaying tokens to L2.
     */
    constructor(
        IL1GatewayRouter _l1GatewayRouter,
        IL1ScrollMessenger _l1ScrollMessenger,
        IL2GasPriceOracle _l2GasPriceOracle,
        uint32 _l2MessageRelayGasLimit,
        uint32 _l2TokenRelayGasLimit
    ) {
        L1_GATEWAY_ROUTER = _l1GatewayRouter;
        L1_SCROLL_MESSENGER = _l1ScrollMessenger;
        L2_GAS_PRICE_ORACLE = _l2GasPriceOracle;
        L2_MESSAGE_RELAY_GAS_LIMIT = _l2MessageRelayGasLimit;
        L2_TOKEN_RELAY_GAS_LIMIT = _l2TokenRelayGasLimit;
    }

    /**
     * @notice Send message to `target` on Scroll.
     * @dev This message is marked payable because relaying the message will require
     * a fee that needs to be propagated to the Scroll Bridge. It will not send msg.value
     * to the target contract on L2.
     * @param target L2 address to send message to.
     * @param message Message to send to `target`.
     */
    function relayMessage(address target, bytes calldata message) external payable {
        // We can specifically send a message with 0 value to the Scroll Bridge
        // and it will not forward any ETH to the target contract on L2. However,
        // we need to set the payable value to msg.value to ensure that the Scroll
        // Bridge has enough gas to forward the message to L2.
        L1_SCROLL_MESSENGER.sendMessage{ value: _generateRelayerFee(L2_MESSAGE_RELAY_GAS_LIMIT) }(
            target,
            0,
            message,
            L2_MESSAGE_RELAY_GAS_LIMIT
        );
        emit MessageRelayed(target, message);
    }

    /**
     * @notice Send `amount` of `l1Token` to `to` on Scroll. `l2Token` is the Scroll address equivalent of `l1Token`.
     * @dev This method is marked payable because relaying the message might require a fee
     * to be paid by the sender to forward the message to L2. However, it will not send msg.value
     * to the target contract on L2.
     * @param l1Token L1 token to bridge.
     * @param l2Token L2 token to receive.
     * @param amount Amount of `l1Token` to bridge.
     * @param to Bridge recipient.
     */
    function relayTokens(
        address l1Token,
        address l2Token,
        uint256 amount,
        address to
    ) external payable {
        IL1GatewayRouter _l1GatewayRouter = L1_GATEWAY_ROUTER;

        // Confirm that the l2Token that we're trying to send is the correct counterpart
        // address
        address _l2Token = _l1GatewayRouter.getL2ERC20Address(l1Token);
        require(_l2Token == l2Token, "l2Token Mismatch");

        IERC20(l1Token).safeIncreaseAllowance(address(_l1GatewayRouter), amount);

        // The scroll bridge handles arbitrary ERC20 tokens and is mindful of
        // the official WETH address on-chain. We don't need to do anything specific
        // to differentiate between WETH and a separate ERC20.
        // Note: This happens due to the L1GatewayRouter.getERC20Gateway() call
        // Note: dev docs: https://docs.scroll.io/en/developers/l1-and-l2-bridging/eth-and-erc20-token-bridge/
        _l1GatewayRouter.depositERC20{ value: _generateRelayerFee(L2_TOKEN_RELAY_GAS_LIMIT) }(
            l1Token,
            to,
            amount,
            L2_TOKEN_RELAY_GAS_LIMIT
        );
        emit TokensRelayed(l1Token, l2Token, amount, to);
    }

    /**************************************
     *         INTERNAL FUNCTIONS         *
     **************************************/

    /**
     * @notice Generates the relayer fee for a message to be sent to L2.
     * @dev Function will revert if the contract does not have enough ETH to pay the fee.
     * @param _l2GasLimit Gas limit for relaying message to L2.
     * @return l2Fee The relayer fee for the message.
     */
    function _generateRelayerFee(uint32 _l2GasLimit) internal view returns (uint256 l2Fee) {
        l2Fee = L2_GAS_PRICE_ORACLE.estimateCrossDomainMessageFee(_l2GasLimit);
        require(address(this).balance >= l2Fee, "Insufficient ETH balance");
    }
}

contracts/chain-adapters/Succinct_Adapte.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "./interfaces/AdapterInterface.sol";
import "../external/interfaces/SuccinctInterfaces.sol";

// solhint-disable-next-line contract-name-camelcase
contract Succinct_Adapter is AdapterInterface {
    ITelepathyBroadcaster public immutable succinctSourceAmb;
    uint16 public immutable destinationChainId;

    // Special Succinct event for additional tracking information.
    event SuccinctMessageRelayed(bytes32 messageRoot, uint16 destinationChainId, address target, bytes message);

    /**
     * @notice Constructs new Adapter.
     * @param _succinctSourceAmb address of the SourceAmb succinct contract for sending messages.
     * @param _destinationChainId chainId of the destination.
     */
    constructor(ITelepathyBroadcaster _succinctSourceAmb, uint16 _destinationChainId) {
        succinctSourceAmb = _succinctSourceAmb;
        destinationChainId = _destinationChainId;
    }

    /**
     * @notice Send cross-chain message to target on the destination.
     * @param target Contract on the destination that will receive the message.
     * @param message Data to send to target.
     */
    function relayMessage(address target, bytes calldata message) external payable override {
        bytes32 messageRoot = succinctSourceAmb.send(destinationChainId, target, message);

        // Note: this emits two events. MessageRelayed for the sake of compatibility with other adapters.
        // It emits SuccinctMessageRelayed to encode additional tracking information that is Succinct-specific.
        emit MessageRelayed(target, message);
        emit SuccinctMessageRelayed(messageRoot, destinationChainId, target, message);
    }

    /**
     * @notice No-op relay tokens method.
     */
    function relayTokens(
        address,
        address,
        uint256,
        address
    ) external payable override {
        // This method is intentionally left as a no-op.
        // If the adapter is intended to be able to relay tokens, this method should be overriden.
    }
}

contracts/chain-adapters/ZkSync_Adapter..content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "./interfaces/AdapterInterface.sol";
import "../external/interfaces/WETH9Interface.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface ZkSyncInterface {
    // _contractL2: L2 address of the contract to be called.
    // _l2Value: Amount of ETH to pass with the call to L2; used as msg.value for the transaction.
    // _calldata: Calldata of the transaction call; encoded the same way as in Ethereum.
    // _l2GasLimit: Gas limit of the L2 transaction call.
    // _l2GasPerPubdataByteLimit: A constant representing how much gas is required to publish a byte of data from
    //  L1 to L2. https://era.zksync.io/docs/api/js/utils.html#gas
    // _factoryDeps: Bytecodes array containing the bytecode of the contract being deployed.
    //  If the contract is a factory contract, the array contains the bytecodes of the contracts it can deploy.
    // _refundRecipient: Address that receives the rest of the fee after the transaction execution.
    //  If refundRecipient == 0, L2 msg.sender is used. Note: If the _refundRecipient is a smart contract,
    //  then during the L1 to L2 transaction its address is aliased.
    function requestL2Transaction(
        address _contractL2,
        uint256 _l2Value,
        bytes calldata _calldata,
        uint256 _l2GasLimit,
        uint256 _l2GasPerPubdataByteLimit,
        bytes[] calldata _factoryDeps,
        address _refundRecipient
    ) external payable returns (bytes32 canonicalTxHash);

    // @notice Estimates the cost in Ether of requesting execution of an L2 transaction from L1
    // @param _l1GasPrice Effective gas price on L1 (priority fee + base fee)
    // @param _l2GasLimit Gas limit for the L2 transaction
    // @param _l2GasPerPubdataByteLimit Gas limit for the L2 transaction per byte of pubdata
    // @return The estimated L2 gas for the transaction to be paid
    function l2TransactionBaseCost(
        uint256 _l1GasPrice,
        uint256 _l2GasLimit,
        uint256 _l2GasPerPubdataByteLimit
    ) external view returns (uint256);
}

interface ZkBridgeLike {
    // @dev: Use ZkSyncInterface.requestL2Transaction to bridge WETH as ETH to L2.
    function deposit(
        address _l2Receiver,
        address _l1Token,
        uint256 _amount,
        uint256 _l2TxGasLimit,
        uint256 _l2TxGasPerPubdataByte,
        address _refundRecipient
    ) external payable returns (bytes32 txHash);
}

// Note: this contract just forwards the calls from the HubPool to ZkSync to avoid limits.
// A modified ZKSync_Adapter should be deployed with this address swapped in for all zkSync addresses.
contract LimitBypassProxy is ZkSyncInterface, ZkBridgeLike {
    using SafeERC20 for IERC20;
    ZkSyncInterface public constant zkSync = ZkSyncInterface(0x32400084C286CF3E17e7B677ea9583e60a000324);
    ZkBridgeLike public constant zkErc20Bridge = ZkBridgeLike(0x57891966931Eb4Bb6FB81430E6cE0A03AAbDe063);

    function l2TransactionBaseCost(
        uint256 _l1GasPrice,
        uint256 _l2GasLimit,
        uint256 _l2GasPerPubdataByteLimit
    ) external view returns (uint256) {
        return zkSync.l2TransactionBaseCost(_l1GasPrice, _l2GasLimit, _l2GasPerPubdataByteLimit);
    }

    function requestL2Transaction(
        address _contractL2,
        uint256 _l2Value,
        bytes calldata _calldata,
        uint256 _l2GasLimit,
        uint256 _l2GasPerPubdataByteLimit,
        bytes[] calldata _factoryDeps,
        address _refundRecipient
    ) external payable returns (bytes32 canonicalTxHash) {
        return
            zkSync.requestL2Transaction{ value: msg.value }(
                _contractL2,
                _l2Value,
                _calldata,
                _l2GasLimit,
                _l2GasPerPubdataByteLimit,
                _factoryDeps,
                _refundRecipient
            );
    }

    function deposit(
        address _l2Receiver,
        address _l1Token,
        uint256 _amount,
        uint256 _l2TxGasLimit,
        uint256 _l2TxGasPerPubdataByte,
        address _refundRecipient
    ) external payable returns (bytes32 txHash) {
        IERC20(_l1Token).safeIncreaseAllowance(address(zkErc20Bridge), _amount);
        return
            zkErc20Bridge.deposit{ value: msg.value }(
                _l2Receiver,
                _l1Token,
                _amount,
                _l2TxGasLimit,
                _l2TxGasPerPubdataByte,
                _refundRecipient
            );
    }
}

/**
 * @notice Contract containing logic to send messages from L1 to ZkSync.
 * @dev Public functions calling external contracts do not guard against reentrancy because they are expected to be
 * called via delegatecall, which will execute this contract's logic within the context of the originating contract.
 * For example, the HubPool will delegatecall these functions, therefore its only necessary that the HubPool's methods
 * that call this contract's logic guard against reentrancy.
 */

// solhint-disable-next-line contract-name-camelcase
contract ZkSync_Adapter is AdapterInterface {
    using SafeERC20 for IERC20;

    // We need to pay a base fee to the operator to include our L1 --> L2 transaction.
    // https://era.zksync.io/docs/dev/developer-guides/bridging/l1-l2.html#getting-the-base-cost

    // Generally, the following params are a bit hard to set and may change in the future once ZkSync
    // goes live. For now, we'll hardcode these and use aggressive values to ensure inclusion.

    // Limit on L2 gas to spend.
    uint256 public constant L2_GAS_LIMIT = 2_000_000;

    // How much gas is required to publish a byte of data from L1 to L2. 800 is the required value
    // as set here https://github.com/matter-labs/era-contracts/blob/6391c0d7bf6184d7f6718060e3991ba6f0efe4a7/ethereum/contracts/zksync/facets/Mailbox.sol#L226
    // Note, this value can change and will require an updated adapter.
    uint256 public constant L1_GAS_TO_L2_GAS_PER_PUB_DATA_LIMIT = 800;

    // This address receives any remaining fee after an L1 to L2 transaction completes.
    // If refund recipient = address(0) then L2 msg.sender is used, unless msg.sender is a contract then its address
    // gets aliased.
    address public immutable l2RefundAddress;

    // Hardcode the following ZkSync system contract addresses to save gas on construction. This adapter can be
    // redeployed in the event that the following addresses change.

    // Main contract used to send L1 --> L2 messages. Fetchable via `zks_getMainContract` method on JSON RPC.
    ZkSyncInterface public constant zkSyncMessageBridge = ZkSyncInterface(0x32400084C286CF3E17e7B677ea9583e60a000324);

    // Contract used to send ETH to L2. Note: this is the same address as the main contract, but separated to allow
    // only this contract to be swapped (leaving the main zkSync contract to be used for messaging).
    ZkSyncInterface public constant zkSyncEthBridge = ZkSyncInterface(0x32400084C286CF3E17e7B677ea9583e60a000324);

    // Bridges to send ERC20 and ETH to L2. Fetchable via `zks_getBridgeContracts` method on JSON RPC.
    ZkBridgeLike public constant zkErc20Bridge = ZkBridgeLike(0x57891966931Eb4Bb6FB81430E6cE0A03AAbDe063);

    // Set l1Weth at construction time to make testing easier.
    WETH9Interface public immutable l1Weth;

    event ZkSyncMessageRelayed(bytes32 canonicalTxHash);

    /**
     * @notice Constructs new Adapter.
     * @param _l1Weth WETH address on L1.
     * @param _l2RefundAddress address that recieves excess gas refunds on L2.
     */
    constructor(WETH9Interface _l1Weth, address _l2RefundAddress) {
        l1Weth = _l1Weth;
        l2RefundAddress = _l2RefundAddress;
    }

    /**
     * @notice Send cross-chain message to target on ZkSync.
     * @notice This contract must hold at least getL1CallValue() amount of ETH to send a message, or the message
     * will revert.
     * @param target Contract on L2 that will receive message.
     * @param message Data to send to target.
     */
    function relayMessage(address target, bytes memory message) external payable override {
        uint256 txBaseCost = _contractHasSufficientEthBalance();

        // Returns the hash of the requested L2 transaction. This hash can be used to follow the transaction status.
        bytes32 canonicalTxHash = zkSyncMessageBridge.requestL2Transaction{ value: txBaseCost }(
            target,
            // We pass no ETH with the call, otherwise we'd need to add to the txBaseCost this value.
            0,
            message,
            L2_GAS_LIMIT,
            L1_GAS_TO_L2_GAS_PER_PUB_DATA_LIMIT,
            new bytes[](0),
            l2RefundAddress
        );

        emit MessageRelayed(target, message);
        emit ZkSyncMessageRelayed(canonicalTxHash);
    }

    /**
     * @notice Bridge tokens to ZkSync.
     * @notice This contract must hold at least getL1CallValue() amount of ETH to send a message
     * or the message will revert.
     * @param l1Token L1 token to deposit.
     * @param l2Token L2 token to receive.
     * @param amount Amount of L1 tokens to deposit and L2 tokens to receive.
     * @param to Bridge recipient.
     */
    function relayTokens(
        address l1Token,
        address l2Token, // l2Token is unused.
        uint256 amount,
        address to
    ) external payable override {
        // This could revert if the relay amount is over the ZkSync deposit
        // limit: https://github.com/matter-labs/era-contracts/blob/main/ethereum/contracts/common/AllowList.sol#L150
        // We should make sure that the limit is either set very high or we need to do logic
        // that splits the amount to deposit into multiple chunks. We can't have
        // this function revert or the HubPool will not be able to proceed to the
        // next bundle. See more here:
        // https://github.com/matter-labs/era-contracts/blob/main/docs/Overview.md#deposit-limitation
        // https://github.com/matter-labs/era-contracts/blob/6391c0d7bf6184d7f6718060e3991ba6f0efe4a7/ethereum/contracts/zksync/facets/Mailbox.sol#L230
        uint256 txBaseCost = _contractHasSufficientEthBalance();

        // If the l1Token is WETH then unwrap it to ETH then send the ETH to the standard bridge along with the base
        // cost. I've tried sending WETH over the erc20Bridge directly but we receive the wrong WETH
        // on the L2 side. So, we need to unwrap the WETH into ETH and then send.
        bytes32 txHash;
        if (l1Token == address(l1Weth)) {
            l1Weth.withdraw(amount);
            // We cannot call the standard ERC20 bridge because it disallows ETH deposits.
            txHash = zkSyncEthBridge.requestL2Transaction{ value: txBaseCost + amount }(
                to,
                amount,
                "",
                L2_GAS_LIMIT,
                L1_GAS_TO_L2_GAS_PER_PUB_DATA_LIMIT,
                new bytes[](0),
                l2RefundAddress
            );
        } else {
            IERC20(l1Token).safeIncreaseAllowance(address(zkErc20Bridge), amount);
            txHash = zkErc20Bridge.deposit{ value: txBaseCost }(
                to,
                l1Token,
                amount,
                L2_GAS_LIMIT,
                L1_GAS_TO_L2_GAS_PER_PUB_DATA_LIMIT,
                l2RefundAddress
            );
        }

        emit TokensRelayed(l1Token, l2Token, amount, to);
        emit ZkSyncMessageRelayed(txHash);
    }

    /**
     * @notice Returns required amount of ETH to send a message.
     * @return amount of ETH that this contract needs to hold in order for relayMessage to succeed.
     */
    function getL1CallValue() public view returns (uint256) {
        // - tx.gasprice returns effective_gas_price. It's also used by Mailbox contract to estimate L2GasPrice
        // so using tx.gasprice should always pass this check that msg.value >= baseCost + _l2Value
        // https://github.com/matter-labs/era-contracts/blob/6391c0d7bf6184d7f6718060e3991ba6f0efe4a7/ethereum/contracts/zksync/facets/Mailbox.sol#L273
        // - priority_fee_per_gas = min(transaction.max_priority_fee_per_gas, transaction.max_fee_per_gas - block.base_fee_per_gas)
        // - effective_gas_price = priority_fee_per_gas + block.base_fee_per_gas
        return
            zkSyncMessageBridge.l2TransactionBaseCost(tx.gasprice, L2_GAS_LIMIT, L1_GAS_TO_L2_GAS_PER_PUB_DATA_LIMIT);
    }

    function _contractHasSufficientEthBalance() internal view returns (uint256 requiredL1CallValue) {
        requiredL1CallValue = getL1CallValue();
        require(address(this).balance >= requiredL1CallValue, "Insufficient ETH balance");
    }
}

contracts/erc1155/MintableERC1155.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";

/**
 * @title MintableERC1155
 * @notice Ownable contract enabling owner to airdrop many recipients the same token ID at once
 */
contract MintableERC1155 is ERC1155, Ownable {
    // Maps `tokenId` to metadata URI `tokenURI`
    mapping(uint256 => string) public _tokenURIs;

    event Airdrop(address caller, uint256 tokenId, address[] recipients, uint256 amount);

    // We are passing an empty string as the `baseURI` because we use `_tokenURIs` instead
    // to allow for IPFS URIs.
    // solhint-disable-next-line
    constructor() ERC1155("") {}

    /**
     * @notice Creates `amount` new tokens for `recipients` of token type `tokenId`.
     * @dev Call might run out of gas if `recipients` arg too long. Might need to chunk up the list.
     * @param recipients List of airdrop recipients.
     * @param tokenId Token type to airdrop.
     * @param amount Amount of token types to airdrop.
     */
    function airdrop(
        uint256 tokenId,
        address[] memory recipients,
        uint256 amount
    ) public onlyOwner {
        for (uint256 i = 0; i < recipients.length; i++) {
            _mint(recipients[i], tokenId, amount, "");
        }
        emit Airdrop(_msgSender(), tokenId, recipients, amount);
    }

    /**
     * @notice Sets the URI for token of type `tokenId` to `tokenURI`.
     * @param tokenId Token type to set `tokenURI` for.
     * @param tokenURI URI of token metadata.
     */
    function setTokenURI(uint256 tokenId, string memory tokenURI) external onlyOwner {
        require(bytes(_tokenURIs[tokenId]).length == 0, "uri already set");

        _tokenURIs[tokenId] = tokenURI;
        emit URI(tokenURI, tokenId);
    }

    /**
     * @notice Returns metadata URI of token type `tokenId`.
     * @dev Instead of returning the same URI for *all* token types, we return the uri set by
     * `setTokenURI` to allow IPFS URIs for all token types.
     * @param tokenId Token type to retrieve metadata URI for.
     */
    function uri(uint256 tokenId) public view override returns (string memory) {
        return _tokenURIs[tokenId];
    }
}

contracts/Ethereum_SpokePool.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "./SpokePool.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

/**
 * @notice Ethereum L1 specific SpokePool. Used on Ethereum L1 to facilitate L2->L1 transfers.
 */
contract Ethereum_SpokePool is SpokePool, OwnableUpgradeable {
    using SafeERC20Upgradeable for IERC20Upgradeable;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor(
        address _wrappedNativeTokenAddress,
        uint32 _depositQuoteTimeBuffer,
        uint32 _fillDeadlineBuffer
    ) SpokePool(_wrappedNativeTokenAddress, _depositQuoteTimeBuffer, _fillDeadlineBuffer) {} // solhint-disable-line no-empty-blocks

    /**
     * @notice Construct the Ethereum SpokePool.
     * @dev crossDomainAdmin is unused on this contract.
     * @param _initialDepositId Starting deposit ID. Set to 0 unless this is a re-deployment in order to mitigate
     * relay hash collisions.
     * @param _hubPool Hub pool address to set. Can be changed by admin.
     */
    function initialize(uint32 _initialDepositId, address _hubPool) public initializer {
        __Ownable_init();
        __SpokePool_init(_initialDepositId, _hubPool, _hubPool);
    }

    /**************************************
     *          INTERNAL FUNCTIONS           *
     **************************************/

    function _bridgeTokensToHubPool(uint256 amountToReturn, address l2TokenAddress) internal override {
        IERC20Upgradeable(l2TokenAddress).safeTransfer(hubPool, amountToReturn);
    }

    // The SpokePool deployed to the same network as the HubPool must be owned by the HubPool.
    // A core assumption of this contract system is that the HubPool is deployed on Ethereum.
    function _requireAdminSender() internal override onlyOwner {}
}

